mapper.select:
SELECT o.order_seq, o.order_date, d.prod_title, p.pay_total_money as total_cost FROM pay as p, order_list as o , delivery as d
WHERE o.order_seq = p.order_seq AND o.order_seq = d.order_seq AND o.user_id = #{user_id}
order by o.order_date desc limit 5;

Select * 
from (select inq_seq, inq_title, inq_date, inq_email, '문의사항' as table_name from inquiry union select review_seq, review_title, review_date, review_email, '후기' as table_name from review) as u 
where u.inq_email=#{user_email} 
order by u.inq_date desc limit 5;

SELECT sum(pa.pay_total_money)
FROM order_list ol , pay pa
WHERE ol.order_seq = pa.order_seq AND ol.order_status = '구매확정' AND user_id = #{user_id}
